data.script:
var self = this;
var showDelay = parseInt('0' || '0', 10);
setTimeout(function () {
    self.sdk.track('banner', getEventProperties('show', false));
    requestAnimationFrame(function () {
        document.body.insertAdjacentHTML('afterbegin', self.html);
        var banner = self.banner = document.querySelector('.exponea-leaderboard');
        banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
        var btnClose = banner.querySelector('.exponea-close');
        self.sdk.trackLink(banner, 'banner', getEventProperties('click'));
        btnClose.onclick = function (e) {
            removeBanner();
            self.sdk.track('banner', getEventProperties('close'));
            e.preventDefault();
            if (e.stopPropagation) {
                e.stopPropagation();
            } else {
                e.cancelBubble = true;
            }
            return false;
        }
    });
}, showDelay);

function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
        banner_type: self.data.banner_type,
        variant_id: self.data.variant_id,
        variant_name: self.data.variant_name,
        interaction: interactive !== false ? true : false,
        location: window.location.href,
        path: window.location.pathname
    };
}

function removeBanner() {
    if (self.banner) {
        self.banner.parentNode.removeChild(self.banner);
    }
}
return {
    remove: removeBanner
};

data.style:
.exponea-leaderboard {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    min-height: 55px;
    z-index: 9999999;
    background: #1c1733;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    line-height: normal;
    text-decoration: inherit;
    text-align: left;
    cursor: pointer;
}

.exponea-leaderboard,
.exponea-leaderboard * {
    box-sizing: content-box;
}

.exponea-leaderboard:hover,
.exponea-leaderboard:active {
    text-decoration: inherit;
}

.exponea-leaderboard span {
    display: inline-block;
}

.exponea-leaderboard .exponea-text {
    font-size: 17px;
    font-weight: 500;
    color: #ffffff;
    margin: 24px;
}

.exponea-leaderboard .exponea-text strong {
    color: #ffcd00;
}

.exponea-leaderboard .exponea-button {
    display: inline-block;
    font-size: 14px;
    color: #665200;
    background: #ffcd00;
    padding: 7px 30px;
    border-radius: 50px;
    -moz-border-radius: 50px;
    -webkit-border-radius: 50px;
    border: 0px solid #800000;
    position: absolute;
    top: 24px;
    right: 38px;
    cursor: pointer;
}

.exponea-leaderboard .exponea-close {
    padding: 15px;
    position: absolute;
    top: 0px;
    right: 0px;
}

.exponea-leaderboard .exponea-close .exponea-close-cross {
    position: absolute;
    display: inline-block;
    width: 14px;
    height: 14px;
    overflow: hidden;
    top: 8px;
    right: 8px;
}

.exponea-leaderboard .exponea-close:hover {
    cursor: pointer;
}

.exponea-leaderboard .exponea-close .exponea-close-cross:before,
.exponea-leaderboard .exponea-close .exponea-close-cross:after {
    content: '';
    position: absolute;
    height: 2px;
    width: 100%;
    top: 50%;
    left: 0;
    margin-top: -1px;
    background: #ffffff;
    height: 1px;
}

.exponea-leaderboard .exponea-close .exponea-close-cross:before {
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
}

.exponea-leaderboard .exponea-close .exponea-close-cross:after {
    -webkit-transform: rotate(45deg);
    transform: rotate(-45deg);
}

@media (max-width: 660px) {
    .exponea-leaderboard {
        text-align: center;
    }
    .exponea-leaderboard .exponea-button {
        position: static;
        display: block;
        max-width: 200px;
        margin: 0 auto 15px;
    }
}

.exponea-branding,
.exponea-branding:hover {
    font-size: 11px;
    color: white;
    opacity: 0.6;
    padding: 5px 0px 5px 0px;
    display: block;
    text-decoration: none;
}

.exponea-branding:hover {
    opacity: 0.9;
    text-decoration: none;
}
